.menu-overlay--on {
  animation: overlayOn 0.5s cubic-bezier(0.215, 0.61, 0.355, 1) forwards;
}

.menu-overlay--off {
  animation: overlayOff 0.5s cubic-bezier(0.215, 0.61, 0.355, 1);
}

@keyframes overlayOn {
  from {
    visibility: hidden;
    opacity: 0;
  }
  to {
    visibility: visible;
    opacity: 1;
  }
}
@keyframes overlayOff {
  from {
    visibility: visible;
    opacity: 1;
  }
  to {
    visibility: hidden;
    opacity: 0;
  }
}

.slideIn {
  opacity: 1 !important;
}

.fadeIn {
  animation: fadein 1.5s ease-in forwards !important;
}

@keyframes fadein {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

.opacity {
  opacity: 1 !important;
}

sources.scss\\_navbar.scss:
// Navbar
#navbar {
  position: fixed;
  top: 0;
  z-index: 5;
  width: 20%;
  height: 100vh;
  // background-color: rgba(238, 237, 237, 0.99);
  background-color: rgba(36, 36, 36, 1);

  // Nav Slide Out
  transform: translateX(-100%);
  transition: all 0.5s cubic-bezier(0.215, 0.61, 0.355, 1);

  .main-nav {
    display: flex;
    flex-direction: column;

    .nav-logo {
      padding-left: 3.5rem;
      padding: 2rem 2rem 2rem 3.5rem;
      display: flex;
      justify-content: space-between;
      overflow: hidden;

      .brand {
        cursor: pointer;
        align-self: center;
        display: flex;

        i {
          color: color(primary);
          padding: 0.75rem 0.75rem 0.75rem 0;
          font-size: 2.5rem;
        }

        &__name {
          align-self: center;
          font-size: 1.75rem;
          padding-right: 1rem;
          color: color(secondary);
        }
      }

      .menu-close {
        cursor: pointer;
        // border: 1px solid red;
        padding: 1rem 1.5rem;
        font-size: 2rem;
        color: color(primary);
        transition: all 0.3s ease-in;
        &:hover {
          color: color(primaryOverlay);
        }
      }
    }

    .nav-item {
      margin-top: 0.5rem;
      padding: 2rem 3.5rem;
      transition: all 0.2s ease-in;

      .nav-link {
        padding: 1rem 0;
        font-size: 1.5rem;
        text-transform: capitalize;
        letter-spacing: 0.2rem;
        color: color(primary);
      }

      &:hover {
        background-color: color(primaryOverlay);
      }
      &--active {
        background-color: color(secondaryOverlay);
        .nav-link {
          color: color(primary);
        }
      }

      &:hover > .nav-link {
        color: color(secondary);
      }
    }
  }
}

sources.scss\\_header.scss:
//Section - Header
header {
  z-index: 3;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  background-color: color(bgColor);
  transition: all 0.5s ease-in;
  // border-bottom: 1px solid color(border);
  box-shadow: 0px 1px 1px color(border);

  .navigation {
    padding: 1.5rem 0;
    display: flex;
    justify-content: space-between;
    color: color(secondary);

    .menu-btn {
      align-self: center;
      cursor: pointer;
    }

    .brand {
      align-self: center;
      display: flex;

      i {
        color: color(primary);
        padding: 0.75rem;
        font-size: 4rem;
      }

      &__name {
        align-self: center;
        font-size: 2rem;
      }
    }

    .user-info {
      display: flex;
      align-self: center;

      &__wrapper {
        display: flex;
        padding: 1rem 1.5rem;
        border: 1px solid color(border);
        position: relative;

        &__cart__count {
          position: absolute;
          top: -1rem;
          right: 0;
          padding: 0.5rem 1rem;
          font-size: 1.25rem;
          background-color: color(primary);
          border-radius: 50%;
        }

        // Modifier
        &--cart {
          margin-right: 0.5rem;
        }
      }

      a {
        align-self: flex-end;
        color: color(secondary);

        i {
          margin: 0 0.25rem;
        }

        span {
          font-size: 1.5rem;
        }
      }
    }
  }
}

