// Section links
#links {
  padding: 5rem 0;

  .links {
    color: color(secondary);
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    font-size: 2rem;
    line-height: 2.5;

    h3 {
      padding-bottom: 1rem;
      font-size: 2rem;
    }
    li {
      font-size: 1.5rem;
      text-transform: capitalize;

      a {
        color: color(secondaryOverlay);
        transition: all 0.5s ease-in;

        &:hover {
          color: color(primary);
        }

        i {
          padding: 1rem;
          color: color(primary);
          background: color(secondary);
          border-radius: 50%;
          font-size: 2rem;
          margin: 0.5rem 0.5rem 0 0;
        }
      }
    }
  }
}

// Section - Footer
footer {
  padding: 2rem 0;
  .footer-inner {
    padding: 2rem 0;
    text-align: center;
    font-size: 1.75rem;
    text-transform: capitalize;
    border-top: 1px solid color(border);
    border-bottom: 1px solid color(border);

    span {
      padding-left: 0.5rem;
      color: color(primary);
    }
  }
}

