//**^^ Home page content Start ^^**//
// Section - Showcase
#showcase {
  margin-top: 8.5rem;
  height: 90vh;
  background: linear-gradient(rgba(0, 0, 0, 0.75), rgba(0, 0, 0, 0.75)),
    url(../img/products/Acer_Predator_21X-2.JPG);
  background-position: center center;
  background-repeat: no-repeat;
  background-size: cover;
  .showcase {
    height: 100%;
    color: color(secondary);
    display: flex;
    flex-direction: column;
    justify-content: center;
    animation: fadein 1s ease-in forwards;

    &__title {
      font-size: 5rem;
      text-transform: capitalize;
    }

    &__text {
      padding: 0.5rem 0;
      font-size: 2.5rem;
      text-transform: capitalize;

      &--sub {
        .showcase__price {
          font-size: 4rem;
          color: color(primary);
        }
      }
    }

    .btn {
      align-self: flex-start;
      @extend %btn;

      &:hover {
        @extend %btn_hover;
      }
    }
  }
}

// Section - Cards
#cards {
  padding: 5rem 0;
  overflow: hidden;
  border-bottom: 0.5px solid color(border);

  .cards {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: repeat(1, 300px);
    grid-column-gap: 5rem;

    .card {
      padding: 2rem 1.5rem;
      display: flex;
      flex-direction: column;
      justify-content: flex-end;
      border: 0.25rem solid color(secondary);
      transition: all 1s ease;

      &__title {
        font-size: 2.5rem;
        color: color(primary);
        text-transform: capitalize;
      }

      &__text {
        font-size: 2rem;
        color: color(secondary);
        text-transform: capitalize;
      }

      &--1 {
        background: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)),
          url(../img/laps/bg_1.png) center no-repeat;
        background-size: cover;
        //**Bring back via JS**//
        opacity: 0;
        // transform: translateX(-125%) translateY(0);
      }
      &--2 {
        background: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)),
          url(../img/laps/bg_18.jpg) center no-repeat;
        background-size: cover;
        //**Bring back via JS**//
        opacity: 0;
        // transform: translateX(0) translateY(-125%);
      }
      &--3 {
        background: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)),
          url(../img/laps/bg_5.jpg) center no-repeat;
        background-size: cover;
        //**Bring back via JS**//
        opacity: 0;
        // transform: translateX(125%) translateY(0);
      }

      &:hover {
        transform: scale(1.05) !important;
      }
    }
  }
}

// Section - Collection
#collection {
  padding: 7.5rem 0;
  background: linear-gradient(rgba(0, 0, 0, 0.75), rgba(0, 0, 0, 0.75)),
    url(../img/laps/bg_12.jpg) no-repeat center center/cover;
  border-bottom: 0.5px solid color(border);

  .collection {
    opacity: 0;
    display: flex;
    flex-direction: column;
    align-items: center;
    transition: all 1s cubic-bezier(0.165, 0.84, 0.44, 1);

    &__title {
      @extend %title;
    }

    &__text {
      @extend %text;
      margin-bottom: 2.5rem;
    }

    .btn {
      @extend %btn;

      &:hover {
        @extend %btn_hover;
      }
    }

    &__notification {
      padding: 0.75rem 0;
      font-size: 3rem;
      text-transform: capitalize;
      color: color(primary);
    }
  }
}

// Section - Arrivals
#arrival {
  .arrival {
    display: flex;
    min-height: 50vh;

    &__item {
      padding: 7.5rem 5%;
      // padding-left: 10%;
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: flex-start;
      transition: all 1s ease;

      &__title {
        color: color(primary);
        font-size: 3.5rem;
        text-transform: capitalize;
      }
      &__text {
        @extend %text;
      }

      .btn {
        margin-top: 2rem;
        @extend %btn;

        &:hover {
          @extend %btn_hover;
        }
      }

      &--1 {
        flex: 1;
        background: linear-gradient(rgba(0, 0, 0, 0.75), rgba(0, 0, 0, 0.5)),
          url(../img/laps/bg_2.jpg) no-repeat center center/cover;
        //transform: translateX(-100%) translateY(0);
        opacity: 0;

        &:hover {
          flex: 2;
        }
      }
      &--2 {
        flex: 1;
        background: linear-gradient(rgba(0, 0, 0, 0.75), rgba(0, 0, 0, 0.5)),
          url(../img/products/Acer_Predator_21X-3.JPG) no-repeat center
            center/cover;
        //transform: translateX(100%) translateY(0);
        opacity: 0;

        &:hover {
          flex: 2;
        }
      }
    }
  }
}

// Section - Promo
#promo {
  height: 50vh;
  position: relative;

  .promo__content {
    z-index: 2;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translateX(-50%) translateY(-50%);
    display: flex;
    flex-direction: column;
    align-items: center;

    .promo__title {
      @extend %title;
      margin-bottom: 1rem;
    }
    .btn {
      @extend %btn;

      &:hover {
        @extend %btn_hover;
      }
    }
  }

  .promo__overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    z-index: 1;
  }

  .promo__video-wrapper {
    max-width: 100%;
    height: 100%;
    position: relative;

    video {
      width: 100%;
      height: 100%;
      object-fit: inherit;
    }
  }
}
//**^^ Home page content End ^^**//

//**^^ MEDIA QUERY ^^**//
//** Home page Media query start **//
@media (max-width: 575.98px) {
  @include XS;
}

@media (min-width: 576px) and (max-width: 767.98px) {
  @include SM;
}

@media (min-width: 768px) and (max-width: 991.98px) {
  @include MD;
}

@media (min-width: 992px) and (max-width: 1199.98px) {
  @include LG;
}
//** Home page Media query End **//

sources.scss\\_products.scss:
@import 'media';
//**^^ Products page content Start ^^**//

#products {
  margin-top: 8.5rem;
  padding: 5rem 0;
  border: 1px solid color(border);

  h1 {
    color: color(secondary);
    text-align: center;
    @extend %title;
  }

  .search {
    text-align: center;
    width: 50%;
    padding: 2rem 0;

    .form-group {
      display: flex;
      justify-content: center;
      border: none;
      outline: none;
      .form-control {
        flex: 1;
        padding: 1rem;
        border-radius: 1rem;
        font-size: 1.5rem;
        font-family: 'Poppins', sans-serif;

        &::placeholder {
          text-align: center;
          letter-spacing: 0.5rem;
          text-transform: capitalize;
        }
      }

      .search-icon {
        margin-left: -3.75rem;
        padding: 1rem;
        // border-radius: 1rem;
        border-top-right-radius: 1rem;
        border-bottom-right-radius: 1rem;
        font-size: 2rem;
        background-color: color(primary);
        border: none;

        i {
          color: rgba(0, 0, 0, 0.75);
        }
      }
    }
  }

  .products-wrapper {
    //** Layout Split here **//
    display: flex;

    // Filter Section
    .filter {
      flex: 1;
      margin-right: 2.5rem;

      &__item {
        margin-bottom: 1.5rem;

        .page {
          width: 100%;
          padding: 1rem;
          appearance: none;
          font-size: 1.5rem;
          border: none;
          color: color(primary);
          font-family: 'Poppins', sans-serif;
        }

        &-heading {
          border-bottom: 1px solid color(border);
          display: flex;
          justify-content: space-between;
          padding: 0.75rem 0.75rem 0.75rem 0;
          font-size: 1.5rem;
          color: color(secondary);

          i {
            align-self: center;
          }
          h2 {
            color: color(secondary);
          }
        }

        .form-group {
          font-size: 1.5rem;
          padding: 0.5rem 0;
          .form-control {
            display: none;
            //display: block;
          }
          span {
            color: color(secondary);
            transition: all 0.2s ease-in;
          }

          label {
            color: color(secondary);
          }
          .form-control:checked + span {
            color: color(primary);
          }
        }

        &--checkbox {
          .form-group {
            .form-control {
              display: none;
            }

            //By Default empty check
            span {
              i:nth-child(2) {
                display: none;
              }
            }

            // On Check show tick checkbox
            .form-control:checked + span {
              i:nth-child(1) {
                display: none;
              }
              i:nth-child(2) {
                display: inline-block;
              }
            }
          }
        }

        &--rating {
          .form-control:checked + label {
            color: color(primary);
          }
        }
      }

      .btn {
        @extend %btn;
        border: none;
        background-color: color(primary);

        &:hover {
          @extend %btn_hover;
          background-color: color(primaryOverlay);
        }
      }
    }

    //Product Items Section
    .items {
      flex: 4;
      display: grid;
      grid-template-columns: repeat(4, 1fr);
      grid-template-rows: repeat(2, 1fr);
      grid-gap: 2rem;

      &-item {
        position: relative;
        width: 100%;
        height: 100%;
        padding: 1rem;
        transition: all 0.5s ease-in;
        @include outShadow(-2px, -2px, 2px, 2px, 3px);

        // Img
        &__img {
          overflow: hidden;
          position: relative;
          height: 70%;

          img {
            transition: all 0.5s cubic-bezier(0.075, 0.82, 0.165, 1);
            width: 100%;
            height: 100%;
          }

          &::after {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            width: 100%;
            height: 100%;
            transform: translate(-100%, 100%) scale(-1, -5) rotate(180deg);
            transition: all 0.25s ease-in;
            background: rgba(255, 255, 255, 0.1);
          }
        }

        // Text Content
        &__content {
          padding: 1rem 0;
          margin-bottom: 1rem;
          text-align: center;
          color: color(secondary);
          transition: all 0.5s cubic-bezier(0.075, 0.82, 0.165, 1);

          &__title {
            padding: 0.5rem 0;
            text-transform: uppercase;
            font-size: 1.5rem;
            letter-spacing: 0.2rem;
            border-bottom: 1px solid color(border);
          }
          &__price {
            padding: 0.5rem 0;
            font-size: 2rem;
            i {
              color: color(primary);
            }
          }

          &__rating {
            padding: 1.5rem;
            font-size: 1.5rem;
            position: absolute;
            right: 0;
            bottom: 0;
            color: color(primaryOverlay);
          }
        }

        .btn {
          position: absolute;
          top: 50%;
          left: 50%;
          opacity: 0;
          transition: all 0.25s ease-in-out;
          transform: translate(-50%, -50%);
          @extend %btn;

          &:hover {
            @extend %btn_hover;
          }
        }

        // On Item Hover
        &:hover {
          .items-item__img > img {
            transform: scale(1.3);
            filter: blur(2px);
          }

          .items-item__img::after {
            transform: translate(0, 0) scale(1);
          }

          .items-item__content {
            color: color(primary);
          }

          .items-item__content__rating {
            color: color(primary);
          }

          .btn {
            opacity: 1;
          }
        }
      }
    }
  }
}

//**^^ Products page content End ^^**//

//** Media Queries **//
@media (max-width: 575.98px) {
  @include ProductsXS;
}

@media (min-width: 576px) and (max-width: 767.98px) {
  @include ProductsSM;
}

@media (min-width: 768px) and (max-width: 991.98px) {
  @include ProductsMD;
}

@media (min-width: 992px) and (max-width: 1199.98px) {
  @include ProductsLG;
}

