@import 'media';
//**^^ About Us content Start ^^**//

// Repeated Style
@mixin contentStyle {
  &__content {
    padding: 0 5rem;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: color(border);

    &__title {
      @include title(0.5rem, 0, 4rem);
      margin-bottom: 1rem;
      letter-spacing: 0.55rem;

      i {
        color: color(primaryOverlay);
        vertical-align: middle;
      }
    }

    &__text {
      font-size: 1.5rem;
      color: color(secondaryOverlay);
      margin-bottom: 2rem;

      &--highlight {
        color: color(primary);
      }
    }

    .btn {
      @extend %btn;
      &:hover {
        @extend %btn_hover;
      }
    }
  }
}

#about {
  padding-top: 2rem;
  margin-top: 8.5rem;

  .title {
    @extend %title;
    text-align: center;
  }

  .about {
    margin-top: 2rem;
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-template-rows: repeat(1, 650px);

    &__img {
      overflow: hidden;
      position: relative;
      background: linear-gradient(
          to right,
          rgba(0, 0, 0, 0.3),
          rgba(0, 0, 0, 0.2)
        ),
        url(../img/laps/bg_21.jpg) no-repeat center center/cover;
    }

    // about__content.
    @include contentStyle;
  }
}

//   Section -Service
#service {
  .service {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-template-rows: repeat(1, 650px);

    @include contentStyle;

    &__gallery {
      overflow: hidden;
      display: grid;
      grid-template-columns: repeat(2, 1fr);
      grid-template-rows: repeat(2, 50%);

      &__img {
        height: 100%;
        transition: 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275);

        img {
          height: 100%;
          width: 100%;
        }

        &:hover {
          z-index: 2;
          border: 5px solid color(primary);
          filter: sepia(0.2);
          transform: scale(1.05);
        }
      }
    }
  }
}

//**^^ About Us page content End ^^**//

//** Media Queries **//
@media (max-width: 575.98px) {
  @include AboutXS;
}

@media (min-width: 576px) and (max-width: 767.98px) {
  @include AboutSM;
}

@media (min-width: 768px) and (max-width: 991.98px) {
  @include AboutsMD;
}

@media (min-width: 992px) and (max-width: 1199.98px) {
  @include AboutLG;
}

sources.scss\\_contact.scss:
#contact {
  margin-top: 8.5rem;
  .contact {
    padding-bottom: 2.5rem;
    border-bottom: 1px solid color(border);
    .title {
      @include title(2.5rem, 0, 5rem);
      text-align: center;
    }

    .form-title {
      @include title(1rem, 0, 2.5rem);
      color: color(primary);
    }

    &__content {
      display: grid;
      grid-template-columns: repeat(2, 60% 40%);
      grid-gap: 1rem;

      .contact-form {
        //border: 1px solid red;
        padding: 0 1rem;
        .form-control {
          display: block;
          width: 100%;
          margin: 1.5rem 0;
          padding: 1.25rem;
          font-size: 1.5rem;
          font-family: 'Poppins', sans-serif;
        }

        .form-group {
          display: grid;
          grid-template-columns: repeat(2, 1fr);
          grid-gap: 2rem;
          .form-control {
            margin-bottom: 0;
          }
        }

        .btn {
          @include form-btn(0);
          transition: all 0.5s;

          &:hover {
            @extend %form-btn_hover;
          }
        }
      }

      .contact-details {
        padding: 1.25rem 0 0 7.5rem;
        font-size: 2rem;
        text-transform: capitalize;
        color: color(secondary);
        //border: 1px solid red;

        &__item {
          display: grid;
          grid-template-columns: repeat(2, 12.5% 87.5%);
          align-items: center;
          margin-bottom: 2.5rem;
          //border: 1px solid green;

          .icon {
            text-align: center;
            //border: 1px solid yellow;
            i {
              //border: 1px solid yellow;
              font-size: 3.5rem;
              color: color(secondaryOverlay);
            }
          }

          &__info {
            margin-left: 1rem;
            p {
              padding: 0.5rem 0;
            }
          }

          // Modifier
          &--phone {
            .contact-details__item__info {
              p:nth-child(2) {
                color: color(primaryOverlay);
              }
            }
          }

          // Modifier
          &--email {
            .contact-details__item__info {
              p:nth-child(2) {
                color: color(primaryOverlay);
              }
            }
          }
        }
      }
    }
  }
}

//** Media Queries **//
@media (max-width: 575.98px) {
  @include contact(3.5rem, $XSM, 1.5rem);
}

@media (min-width: 576px) and (max-width: 767.98px) {
  @include contact(3.5rem, $XSM, 1.75rem);
}

@media (min-width: 768px) and (max-width: 991.98px) {
  @include contact(5rem, $notXSM, 1.5rem);
}

@media (min-width: 992px) and (max-width: 1199.98px) {
  @include contact(5rem, $notXSM, 1.5rem);
}

sources.scss\\_productDetails.scss:
#detail {
  margin-top: 8.5rem;

  .title {
    @extend %title;
    text-align: center;
    margin-bottom: 2rem;
  }

  .detail {
    padding-bottom: 5rem;
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-template-rows: repeat(1, 400px);
    grid-column-gap: 2rem;
    border-bottom: 1px solid color(border);

    &__img {
      //border: 5px solid red;
      display: grid;
      grid-template-columns: repeat(2, 1fr);
      //grid-template-rows: repeat(1, 400px);
      grid-column-gap: 2rem;

      img {
        width: 100%;
        height: 100%;
        padding: 1rem;
        border: 0.25px solid rgba(168, 116, 26, 0.25);
        box-shadow: 0.5px 0.5px 5px rgba(3, 15, 18, 0.2),
          0.5px 0.5px 5px rgba(3, 15, 18, 0.2);
      }
    }

    &__content {
      position: relative;
      display: flex;
      flex-direction: column;
      justify-content: space-between;

      &__title {
        @include title(0, 0, 4rem);
        position: relative;
        .price {
          position: absolute;
          right: 0;
          font-size: 3rem;
          vertical-align: bottom;
          color: color(primaryOverlay);
        }
      }

      &__description {
        font-size: 1.75rem;
        text-align: left;
        text-transform: capitalize;
        color: color(primary);
        li {
          display: flex;
          justify-content: space-between;
          flex-wrap: wrap;
          .key {
            color: color(secondary);
          }
        }
      }
      .btn {
        // padding: 1.5rem 3.5rem;
        // font-size: 1.5rem;
        // text-transform: capitalize;
        // background-color: color(secondary);
        // color: color(primary);
        margin-top: 1rem;
        @include btn(1rem, 2.5rem, 1.25rem, secondary);
        align-self: flex-start;

        &:hover {
          background-color: color(primary);
          color: color(secondary);
        }
      }

      form {
        position: absolute;
        bottom: 0;
        right: 0;
        .qty-btn {
          appearance: none;
          border: none;
          padding: 1rem 2rem;
          font-size: 1.5rem;
          text-align: center;
          text-transform: capitalize;
          background-color: color(secondary);
          color: color(primary);
        }
      }
    }
  }
}

// Section - Review
#review {
  .title {
    @include title(2rem, 0, 5rem);
    letter-spacing: 0.5rem;
  }

  .review {
    .status-Msg {
      padding: 1rem 0;
      font-size: 1.75rem;
      text-align: center;
      text-transform: capitalize;
      color: color(primary);
    }
    &__item {
      border: 1px solid color(border);
      padding-bottom: 1rem;
      margin-bottom: 1rem;
      &__head {
        padding-bottom: 1rem;
        margin-bottom: 0.5rem;
        display: flex;
        justify-content: space-between;
        font-size: 2.5rem;
        color: color(primary);
        border-bottom: 1px solid color(border);

        &__username,
        &__date {
          padding: 1rem;
          flex: 1;
          border: 1px solid color(border);
        }
        &__username {
          font-size: 2.5rem;
        }
        &__date {
          text-align: right;
        }
      }

      &__body {
        position: relative;
        color: color(secondary);
        padding: 2.5rem 0;
        &__heading {
          padding: 1rem;
          font-size: 2rem;
          display: flex;
          justify-content: space-between;
          text-transform: capitalize;

          .stars {
            color: color(primary);
          }
        }
        &__description {
          padding: 1rem;
          font-size: 1.5rem;
        }
        .edit-review {
          cursor: pointer;
          color: color(primary);
          position: absolute;
          padding: 0.5rem;
          bottom: 0;
          right: 0.5rem;
        }
        .delete-review {
          cursor: pointer;
          color: color(primary);
          position: absolute;
          padding: 0.5rem;
          bottom: 0;
          left: 0.5rem;
        }
      }
    }

    &__item__edit {
      margin: 1rem 0;
      .title {
        @include title(2rem, 0, 2.5rem);
      }
      // Repeated code
      .form-group {
        width: 100%;
        margin-bottom: 1rem;

        label {
          display: inline-block;
          font-size: 2rem;
          padding: 0.25rem 0;
          color: color(primary);
        }
        .form-control {
          width: 100%;
          padding: 1rem;
          font-family: 'Poppins', sans-serif;
        }

        select {
          appearance: none;
          border: none;
          color: color(primary);
        }
      }
      .btn-wrapper {
        margin-top: 2rem;
        display: flex;
        justify-content: space-between;
        .btn {
          border: none;
          background-color: color(primary);
          @extend %btn;
        }
      }
    }
  }

  .review__form {
    padding-bottom: 5rem;
    border-bottom: 1px solid color(border);
    .title {
      @include title(3.5rem, 0, 3rem);
      letter-spacing: 0.5rem;
    }

