// Section Cart
#cart {
  margin-top: 8.5rem;

  .cart {
    display: grid;
    grid-template-columns: 70% 30%;
    grid-gap: 2rem;
    padding-bottom: 5rem;
    border-bottom: 1px solid color(border);

    &__empty-cart {
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
      height: 50vh;
      text-align: center;
      padding-left: 33.33%;

      i {
        font-size: 5rem;
        color: color(primary);
      }
      .title {
        @include title(1rem, 0, 3.5rem);
      }
      .btn {
        @extend %btn;

        &:hover {
          @extend %btn_hover;
        }
      }
    }

    &__items {
      //border: 1px solid red;
      padding-bottom: 7.5rem;
      position: relative;

      .delete-all {
        @extend %btn;
        position: absolute;
        bottom: 0;
        right: 0;

        &:hover {
          @extend %btn_hover;
        }
      }

      &__title {
        @include title(1rem, 0, 3.5rem);
      }

      &__item {
        padding: 2rem 0;
        border-bottom: 1px solid color(border);
        display: grid;
        grid-template-columns: 150px repeat(1, 1fr);
        grid-template-rows: repeat(1, 125px);
        grid-gap: 1rem;

        .img {
          border: 1px solid color(border);
          box-shadow: 1px 1px 1px rgba(0, 0, 0, 0.5);
          padding: 0.5rem;
          img {
            height: 100%;
            width: 100%;
          }
        }

        ul {
          display: flex;
          justify-content: space-around;
          align-items: center;
          flex-wrap: wrap;

          li {
            flex: 1;
            padding: 0.5rem;
            color: color(secondaryOverlay);
            font-size: 1.75rem;
            text-align: center;
            text-transform: capitalize;

            select {
              padding: 0.5rem 2.5rem;
              border: none;
              background-color: color(secondary);
            }

            i {
              cursor: pointer;
              &:hover {
                color: color(secondary);
              }
            }
          }
        }
      }
    }

    &__total {
      margin-top: 2rem;
      align-self: flex-start;
      justify-self: center;
      display: flex;
      flex-direction: column;
      align-items: center;
      border: 1px solid color(border);
      padding: 1.5rem;
      div {
        margin-bottom: 3rem;
        padding-bottom: 2rem;
        color: color(primary);
        border-bottom: 1px solid color(border);
        h3 {
          font-size: 3.5rem;
        }
        p {
          font-size: 1.75rem;
        }
      }
      .btn {
        @extend %btn;
        align-self: center;

        &:hover {
          @extend %btn_hover;
        }
      }
    }
  }
}

//** Media Queries **//
@media (max-width: 991.98px) {
  @include Cart;
}

