              box-shadow: 1px 1px 1px rgba(0, 0, 0, 0.5);
            }
          }

          ul {
            display: flex;
            align-items: center;
            li {
              flex: 1;
              text-align: right;
              text-transform: capitalize;
            }
          }
        }
      }
    }
    // Right Side
    .order-total {
      align-self: flex-start;
      margin-top: 2rem;
      padding: 1.5rem;
      display: flex;
      flex-direction: column;
      border: 1px solid color(border);

      &__content {
        margin-bottom: 3rem;
        padding-bottom: 2rem;
        text-transform: capitalize;
        border-bottom: 1px solid color(border);
        h3 {
          font-size: 3.5rem;
          color: color(primary);
        }
        li {
          padding: 0.5rem 0;
          font-size: 1.75rem;
          color: color(secondary);
          display: flex;
          justify-content: space-between;
        }
      }
      .btn {
        @include form-btn(0);
        align-self: flex-end;

        &:hover {
          @extend %form-btn_hover;
        }
      }
    }
  }
}

//** Media Queries -  **//
@media (max-width: 575.98px) {
  @include editOrder($XSM, 1.5rem, column, flex-end);
}

@media (min-width: 576px) and (max-width: 767.98px) {
  @include editOrder($XSM, 1.75rem, column, flex-end);
}

@media (min-width: 768px) and (max-width: 991.98px) {
  @include editOrder($XSM, 1.75rem, row, center);
}

@media (min-width: 992px) and (max-width: 1199.98px) {
  @include editOrder($notXSM, 2rem, column, flex-end);
}

sources.scss\\_manageProducts.scss:
#manage-products {
  margin-top: 8.5rem;
  .products {
    padding-bottom: 5rem;
    border-bottom: 1px solid color(border);

    .nav {
      padding-top: 2rem;
      display: flex;
      justify-content: space-between;
      .title {
        @include title(2.5rem, 0, 3.5rem);
      }

      .btn {
        align-self: center;
        @include btn(1.5rem, 3rem, 1.25rem, secondary);

        &:hover {
          @include btn_hover(primary);
        }
      }
    }
    .btn {
      @include btn(1.5rem, 3rem, 1.25rem, secondary);

      &:hover {
        @include btn_hover(primary);
      }
    }

    //** Only if not Product to show - Start  **//
    .no-product {
      height: 50vh;
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
      &__text {
        @include title(2rem, 0, 2.75rem);
        color: color(primaryOverlay);
      }
      &__btn {
        @extend %btn;
        &:hover {
          @extend %btn_hover;
        }
      }
    }

    .all-products {
      padding: 1.5rem 0;
      display: grid;
      grid-template-columns: repeat(4, 1fr);
      grid-gap: 1rem;

      .product {
        @include userAndProductCardStyle;
      }
    }
  }
}

// Media Queries
// XS
@media (max-width: 575.98px) {
  @include manageProduct($truthy, $falsy, $falsy);
}

// SM
@media (min-width: 576px) and (max-width: 767.98px) {
  @include manageProduct($truthy, $falsy, $falsy);
}

// MD
@media (min-width: 768px) and (max-width: 991.98px) {
  @include manageProduct($falsy, $truthy, $falsy);
}

// LG - Slight adjustment made on Screen size - 1350.98px nomally it has to be 1199.98px
@media (min-width: 992px) and (max-width: 1350.98px) {
  @include manageProduct($falsy, $falsy, $truthy);
}

