@import 'media';
//**^^ Products page content Start ^^**//

#products {
  margin-top: 8.5rem;
  padding: 5rem 0;
  border: 1px solid color(border);

  h1 {
    color: color(secondary);
    text-align: center;
    @extend %title;
  }

  .products-wrapper {
    //** Layout Split here **//
    //border: 1px solid red;
    display: flex;

    // Filter Section
    .filter {
      //border: 1px solid green;
      flex: 1;
      margin-right: 2.5rem;

      &__item {
        margin-bottom: 1.5rem;

        .page {
          width: 100%;
          padding: 1rem;
          appearance: none;
          font-size: 1.5rem;
          border: none;
          color: color(primary);
          font-family: 'Poppins', sans-serif;
        }

        &-heading {
          border-bottom: 1px solid color(border);
          display: flex;
          justify-content: space-between;
          padding: 0.75rem 0.75rem 0.75rem 0;
          font-size: 1.5rem;
          color: color(secondary);

          i {
            align-self: center;
          }
          h2 {
            color: color(secondary);
          }
        }

        .form-group {
          font-size: 1.5rem;
          padding: 0.5rem 0;
          .form-control {
            display: none;
            //display: block;
          }
          span {
            color: color(secondary);
            transition: all 0.2s ease-in;
          }

          label {
            color: color(secondary);
          }
          .form-control:checked + span {
            color: color(primary);
          }
        }

        &--checkbox {
          .form-group {
            .form-control {
              display: none;
            }

            //By Default empty check
            span {
              i:nth-child(2) {
                display: none;
              }
            }

            // On Check show tick checkbox
            .form-control:checked + span {
              i:nth-child(1) {
                display: none;
              }
              i:nth-child(2) {
                display: inline-block;
              }
            }
          }
        }

        &--rating {
          .form-control:checked + label {
            color: color(primary);
          }
        }
      }

      .btn {
        @extend %btn;
        border: none;
        background-color: color(primary);

        &:hover {
          @extend %btn_hover;
          background-color: color(primaryOverlay);
        }
      }
    }

    //Product Items Section
    .items {
      //border: 1px solid red;
      flex: 4;
      display: grid;
      grid-template-columns: repeat(4, 1fr);
      grid-gap: 2rem;

      &-item {
        //border: 1px solid yellow;
        position: relative;
        width: 100%;
        height: 100%;
        padding: 1rem;
        transition: all 0.5s ease-in;
        @include outShadow(-2px, -2px, 2px, 2px, 3px);

        // Img
        &__img {
          overflow: hidden;
          position: relative;

          img {
            transition: all 0.5s cubic-bezier(0.075, 0.82, 0.165, 1);
            width: 100%;
          }

          &::after {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            width: 100%;
            height: 100%;
            transform: translate(-100%, 100%) scale(-1, -5) rotate(180deg);
            transition: all 0.25s ease-in;
            background: rgba(255, 255, 255, 0.1);
          }
        }

        // Text Content
        &__content {
          padding: 1rem 0;
          margin-bottom: 1rem;
          text-align: center;
          color: color(secondary);
          transition: all 0.5s cubic-bezier(0.075, 0.82, 0.165, 1);

          &__title {
            text-transform: uppercase;
            font-size: 2.25rem;
            letter-spacing: 0.2rem;
            border-bottom: 1px solid color(border);
          }
          &__price {
            padding: 0.5rem 0;
            font-size: 2rem;
            i {
              color: color(primary);
            }
          }
        }

        .btn {
          position: absolute;
          top: 50%;
          left: 50%;
          opacity: 0;
          transition: all 0.25s ease-in-out;
          transform: scale(-2, -5);
          @extend %btn;

          &:hover {
            @extend %btn_hover;
          }
        }

        // On Item Hover
        &:hover {
          .items-item__img > img {
            transform: scale(1.3);
            filter: blur(2px);
          }

          .items-item__img::after {
            transform: translate(0, 0) scale(1);
          }

          .items-item__content {
            color: color(primary);
          }

          .btn {
            opacity: 1;
            transform: translate(-50%, -50%);
          }
        }
      }
    }
  }
}

