// Variables
$XSM: true;
$notXSM: false;

.container {
  max-width: 90%;
  width: 1660px;
  margin: 0 auto;
}

.d-none {
  display: none;
}

// Variables

$colors: (
  primary: rgba(168, 116, 26, 1),
  primaryOverlay: rgba(168, 116, 26, 0.5),
  secondary: rgba(255, 255, 255, 1),
  secondaryOverlay: rgba(255, 255, 255, 0.5),
  cardBorder: rgba(3, 15, 18, 1),
  bgColor: rgba(36, 36, 36, 1),
  border: rgba(51, 51, 51, 1),
);

// Function
@function color($colorName) {
  @return map-get($colors, $colorName);
}

// Inheritance

%btn {
  padding: 1.5rem 3.5rem;
  font-size: 1.5rem;
  color: color(secondary);
  text-transform: capitalize;
  border: 1px solid color(primary);
  transition: all 0.25s ease-in;
}

%btn_hover {
  background-color: color(primary);
}

%title {
  padding: 0.5rem 0;
  color: color(secondary);
  font-size: 5rem;
  text-transform: capitalize;
}

%text {
  padding: 0.75rem 0;
  font-size: 2rem;
  color: color(secondary);
  text-transform: capitalize;
}

@mixin title($py, $px, $fz) {
  padding: $py $px;
  color: color(secondary);
  font-size: $fz;
  text-transform: capitalize;
}

@mixin text($py, $px, $fz) {
  padding: $py $px;
  font-size: $fz;
  color: color(secondary);
  text-transform: capitalize;
}

// Box-shadow
@mixin insetShadow($-x, $-y, $x, $y, $blur) {
  box-shadow: inset $-x $-y $blur rgba(19, 19, 19, 0.5),
    inset $x $y $blur rgba(0, 0, 0, 0.5);
}
@mixin outShadow($-x, $-y, $x, $y, $blur) {
  box-shadow: $-x $-y $blur rgba(19, 19, 19, 0.5),
    $x $y $blur rgba(0, 0, 0, 0.5);
  // transform: translateY(-10px);
}

@mixin form-btn($mt) {
  cursor: pointer;
  padding: 1.5rem 3.5rem;
  font-size: 1.5rem;
  text-transform: capitalize;
  background-color: color(primary);
  color: color(secondary);
  border: none;
  margin-top: $mt;
}

%form-btn_hover {
  background-color: rgba(168, 116, 26, 0.9);
}

// Reusable Auth Style

@mixin auth {
  margin-top: 8.5rem;
  height: 90vh;
  display: flex;
  justify-content: center;
  align-items: center;
  border-bottom: 1px solid color(border);
  background: linear-gradient(rgba(0, 0, 0, 0.9), rgba(0, 0, 0, 0.8)),
    url(../img/laps/bg_21.jpg) no-repeat center center/cover;
  background-attachment: fixed;

  .auth {
    &-wrapper {
      padding: 1rem 2.5rem;
      @include outShadow(-1px, -1px, 1px, 1px, 2px);
    }

    .auth__title {
      @include title(1rem, 1rem, 3rem);
      text-align: center;
    }

    .auth__form {
      label {
        display: inline-block;
        padding: 1rem 0;
        font-size: 1.75rem;
        color: color(primary);
        letter-spacing: 0.25rem;
      }

      .form-control {
        width: 100%;
        display: block;
        padding: 1rem 2rem;
        font-family: 'Poppins', sans-serif;
      }

      .btn {
        @include form-btn(2rem);

        &:active {
          @include insetShadow(-1px, -1px, 1px, 1px, 1px);
        }
      }
    }
    .auth__msg {
      margin-top: 1rem;
      font-size: 1.25rem;
      color: color(secondary);
      text-transform: capitalize;

      p {
        display: flex;
        justify-content: space-between;
      }

      span {
        align-self: center;
      }
      a {
        font-size: 1.5rem;
        color: color(primary);
      }
    }
  }
}

