#review {
  .title {
    @include title(3.5rem, 0, 5rem);
    letter-spacing: 0.5rem;
  }

  .review {
    &__item {
      border: 1px solid color(border);
      padding-bottom: 1rem;
      margin-bottom: 1rem;
      &__head {
        padding-bottom: 1rem;
        margin-bottom: 0.5rem;
        display: flex;
        justify-content: space-between;
        font-size: 2.5rem;
        color: color(primary);
        border-bottom: 1px solid color(border);

        &__username,
        &__date {
          padding: 1rem;
          flex: 1;
          border: 1px solid color(border);
        }
        &__username {
          font-size: 2.5rem;
        }
        &__date {
          text-align: right;
        }
      }

      &__body {
        color: color(secondary);
        &__heading {
          padding: 1rem;
          font-size: 2rem;
          display: flex;
          justify-content: space-between;
          text-transform: capitalize;

          .stars {
            color: color(primary);
          }
        }
        &__description {
          padding: 1rem;
          font-size: 1.5rem;
        }
      }
    }
  }

  .review__form {
    padding-bottom: 5rem;
    border-bottom: 1px solid color(border);
    .title {
      @include title(3.5rem, 0, 3rem);
      letter-spacing: 0.5rem;
    }

    .review-form {
      .form-group {
        width: 100%;
        margin-bottom: 1rem;

