          display: inline-block;
          font-size: 2rem;
          padding: 1rem 0;
          color: color(primary);
        }
        .form-control {
          width: 100%;
          padding: 1rem;
        }

        select {
          appearance: none;
          border: none;
          color: color(primary);
        }
      }

      .btn-wrapper {
        margin-top: 2rem;
        display: flex;
